mapper.select:
	select 

  , case status when '0' then '未发送' when '1' then '已发送' when 'X' then '已取消' else '' end as statusStr
  from t_message
  where 1=1

      and status = #{status}


      and title LIKE CONCAT('%',#{keyWord},'%')


    and sendDate > #{startDate}


    and sendDate < #{endDate}
